import { ADD_PRODUCT } from "../actions/types";

const Reducer = (state = {}, action) => {
    switch (action.type) {
        case ADD_PRODUCT:
            return {...state, products:  [...state.allTodos, action.payload]}
        default:
            return state
    }
};

export default Reducer;
