import { ADD_PRODUCT, FETCH_PRODUCTS, DELETE_PRODUCT, UPDATE_STOCK, UPDATE_PRODUCT_STOCK } from "./types";

FETCH_SEARCH_PRODUCT



export const addProduct = (product) => async (
    dispatch
) => {
    dispatch({
        type: ADD_PRODUCT,
        payload: product
    })
}

export const deleteProduct = (id) => async (
    dispatch
) => {
    const resp = await removeProduct(id)
        dispatch({
            type: DELETE_PRODUCT,
            payload: id
        })
}

export const updateStock = (data) => async (
    dispatch
) => {
    dispatch({
        type: UPDATE_STOCK,
        payload: data
    })
}

export const updateProductStock = (data) => async (
    dispatch
) => {
    const resp = await updateProductAmount(data)
        dispatch({
            type: UPDATE_PRODUCT_STOCK,
            payload: data
        })
}
