import { getProducts } from "../api/productApi";
import { ADD_PRODUCT } from "./types";

export const fetchTodos = () => async (
    dispatch
) => {
    const resp = await getTodos()
        dispatch({
            type: FETCH_TODO,
            payload: resp.data
        })

}

export const addProduct = (product) => async (
    dispatch
) => {
    dispatch({
        type: ADD_PRODUCT,
        payload: product
    })
}
