import { ADD_PRODUCT,  } from "../actions/types";

/* const Reducer = (state = {}, action) => {
    switch (action.type) {
        case ADD_PRODUCT:
            return {...state, products:  [...state.products, action.payload]};
        case FETCH_PRODUCTS:
            return {...state, products: action.payload}
        default:
            return state
    }
}; */

export default (state = {}, action) => {
    switch (action.type) {
        case ADD_PRODUCT:
            return {...state, products:  [...state.products, action.payload]};
      default:
        return state;
    }
  };
